    def openImage(self):
        

        fname = QtWidgets.QFileDialog.getOpenFileName(None, "Select a file...","", filter=" ")
        joined_string = " ".join(fname)
        imagepath=fname[0]
       
        out_image="output.png"
        results=arabicocr.arabic_ocr(imagepath,out_image)
        myfile= open ('outTxt.txt',"w",encoding='utf-8')
        myfile.write("\n")
        #print(results)
        words=[]

        for i in range(len(results)):
            word=results[i][1]
            words.append(word)

        myfile.write(str(words))
        myfile.close()
        
        file = open("outTxt.txt","r",encoding = "utf-8")

        for satir in file:
            a=file.read()

            text3=ku.Hemwar().transliterate(a)

            self.textEdit.setText(a)
            self.textEdit_2.setText(text3)

    def saveOutput(self):
        fname = QtWidgets.QFileDialog.getSaveFileName(None, "Select a file...","", filter=" ")

